//Lab 3 exercise 1//Fall 2014#include<iostream>using namespace std;int population (int prevPop, double birthRate, double deathRate) {    int result = prevPop + birthRate * prevPop - deathRate * prevPop;    return result;}void population2 (int &prevPop, double birthRate, double deathRate) {    prevPop = prevPop + birthRate * prevPop - deathRate * prevPop;}int main(){    int startingPop;    double birthRate, deathRate;    int years;    int pop;        cout << "Please enter the starting size of the population ";    cin >> startingPop;    cout << "Please enter the birth rate and death rate as percentages ";    cin >> birthRate >> deathRate;    cout << "Please enter the number of years to display ";    cin >> years;        //run the loop using call-by-value    pop = startingPop;    cout << "Year 0: " << pop << endl;    for (int i=0; i<years; i++) {        pop = population(pop, birthRate/100, deathRate/100);        cout << "Year " << i+1 << ": " << pop << endl;    }    cout << endl;  //blank line        //run the loop using call-by-reference (doing it twice was optional)    pop = startingPop;    cout << "Year 0: " << pop << endl;    for (int i=0; i<years; i++) {        population2(pop, birthRate/100, deathRate/100);        cout << "Year " << i+1 << ": " << pop << endl;    }}